import "./post.css";
import MoreVertIcon from '@mui/icons-material/MoreVert';
import { useContext, useEffect, useState } from "react";
import axios from "axios";
// import { format } from "timeago.js";
import { Link } from "react-router-dom";
import { AuthContext } from "../../context/AuthContext";

const Post = ({post}) => {
  const instance = axios.create({
    baseURL: 'http://localhost:4000', // Set your custom base URL here
  });
    const [like, setLike] = useState(post.likes.length);
    const [isLiked, setIsLiked] = useState(false);
    const [user, setUser] = useState({});
    const [isLoading, setIsLoading] = useState(true);
    const PF = process.env.REACT_APP_PUBLIC_FOLDER;
    const { user: currentUser } = useContext(AuthContext);

    if (post) setIsLoading(false);


    useEffect(() => {
      setIsLiked(post.likes.includes(currentUser._id));
    }, [currentUser._id, post.likes]);

    useEffect(() => {
      const fetchUser = async () => {
        const res = await instance.get(`/api/users/${post.userId}`);
        setUser(res.data.data);
      };
      fetchUser();
    }, [post.userId]);

    const likeHandler = () => {
      try {
        instance.put("/api/posts/" + post._id + "/like", { userId: currentUser._id });
      } catch (err) {}
      setLike(isLiked ? like - 1 : like + 1);
      setIsLiked(!isLiked);
    };
    if (isLoading) {
      // You can render a loading spinner or message while waiting for the data
      return <p>Loading...</p>;
    }
  return (
    <div className="post">
    <div className="postWrapper">
      <div className="postTop">
        <div className="postTopLeft">
          <Link to={`/profile/${user.username}`}>
            <img
              className="postProfileImg"
              src={
                user.profilePicture
                  ? PF + user.profilePicture
                  : PF + "person/noAvatar.png"
              }
              alt=""
            />
          </Link>
          <span className="postUsername">{user.username}</span>
          <span className="postDate">{(post.createdAt)}</span>
        </div>
        <div className="postTopRight">
          <MoreVertIcon />
        </div>
      </div>
      <div className="postCenter">
        <span className="postText">{post?.desc}</span>
        <img className="postImg" src={PF +'post/'+ post?.img} alt="" />
      </div>
      <div className="postBottom">
        <div className="postBottomLeft">
          <img
            className="likeIcon"
            src={`${PF}like.png`}
            onClick={likeHandler}
            alt=""
          />
          <img
            className="likeIcon"
            src={`${PF}heart.png`}
            onClick={likeHandler}
            alt=""
          />
          <span className="postLikeCounter">{like} people like it</span>
        </div>
        <div className="postBottomRight">
          <span className="postCommentText">{post.comment} comments</span>
        </div>
      </div>
    </div>
  </div>
  );
}
 
export default Post;
